mapper.select:
select id as hospitalId,name as hospitalName from sys_hospital_dictionary where id
in (select hospital_id from sys_hospital where dealers_id = #{entityId,jdbcType=VARCHAR} and del_flag = 0)
and del_flag = 0 order by create_date desc LIMIT #{pageNo},#{pageSize}

select sd.device_id as deviceId,d.device_no as deviceNo,sd.detect_count as detectCount
from sys_device_hospital sd,device d where sd.del_flag = 0 and sd.hospital_id = #{hospitalId,jdbcType=VARCHAR}
and sd.device_id = d.id order by sd.create_date desc LIMIT #{pageNo},#{pageSize}

select count(*)
from sys_device_hospital sd,device d where sd.del_flag = 0 and sd.hospital_id = #{hospitalId,jdbcType=VARCHAR}
and sd.device_id = d.id
