    public static float serialBeanFilter(float x) {
        float y0 = filterOrder2(x, m00, m01, a[0], b, gain[0]);  //
        float y1 = filterOrder2(y0, m10, m11, a[1], b, gain[1]);  //
        float y2 = filterOrder2(y1, m20, m21, a[2], b, gain[2]);  //
        float y3 = filterOrder2(y2, m30, m31, a[3], b, gain[3]);  //
        float y4 = filterOrder2(y3, m40, m41, a[4], b, gain[4]);  //
        float y5 = filterOrder2(y4, m50, m51, a[5], b, gain[5]);  //
        float y6 = filterOrder2(y5, m60, m61, a[6], b, gain[6]);  //
        float y7 = filterOrder2(y6, m70, m71, a[7], b, gain[7]);  //
        float y8 = filterOrder2(y7, m80, m81, a[8], b, gain[8]);  //
        float y9 = filterOrder2(y8, m90, m91, a[9], b, gain[9]);  //

        return y9;
    }
