    public static float serialBeanFilter1(float x) {
        float y0 = filterOrder2(x, n00, n01, na[0], nb, ngain[0]);  //
        float y1 = filterOrder2(y0, n10, n11, na[1], nb, ngain[1]);  //
        float y2 = filterOrder2(y1, n20, n21, na[2], nb, ngain[2]);  //
        float y3 = filterOrder2(y2, n30, n31, na[3], nb, ngain[3]);
        float y4 = filterOrder2(y3, n40, n41, na[4], nb, ngain[4]);  //
        float y5 = filterOrder2(y4, n50, n51, na[5], nb, ngain[5]);
        return y0;
    }

    /*
    x: input
    m1:
    m2:
    a:
    b:
    gain:IIR0dB
    */
    public static float filterOrder2(float x, float[] m1, float[] m2, float[] a, float[] b, float gain){
        float y, m;
        //y_
        m = x - a[1] * m1[0] - a[2] * m2[0];    //my_
        y = m + b[1] * m1[0] + b[2] * m2[0];
        //*m_1*m_2
        m2[0] = m1[0];
        m1[0] = m;
        //
        return y * gain;
    }
